import "./App.css";
import { useState } from "react";



 function App (){
  //useState
  const [restaurantName, SetRestaurantName] = useState("Lemon");

  //Update Function
  function UpdateSetRestaurantName(){
    SetRestaurantName("Little Lemon")
  }

  return (
    <div>
    <h1>{restaurantName}</h1>
    <button onClick={UpdateSetRestaurantName}> Update Restaurant Name</button>
    </div>
  )

};

export default App;
