import "./App.css";
import { useState } from "react";



 function App (){
  //useState
  const [restaurantName, SetRestaurantName] = useState({greeting: "hello"});

  //Update Function
  function UpdateSetRestaurantName(){
    const newName = {...restaurantName}
    newName.greeting = "goodbye";
    SetRestaurantName(newName);
  }

  return (
    <div>
    <h1>{restaurantName}</h1>
    <button onClick={UpdateSetRestaurantName}> Update Restaurant Name</button>
    </div>
  )

};

export default App;
